    public function encrypt($data, $key, $method)
    {
        $ivSize = openssl_cipher_iv_length($method);
        $iv = openssl_random_pseudo_bytes($ivSize);

        $encrypted = openssl_encrypt($data, $method, $key, OPENSSL_RAW_DATA, $iv);

        // For storage/transmission, we simply concatenate the IV and cipher text
        $encrypted = base64_encode($iv . $encrypted);

        return $encrypted;
    }

    public function decrypt($data, $key, $method)
    {
        $data = base64_decode($data);
        $ivSize = openssl_cipher_iv_length($method);
        $iv = substr($data, 0, $ivSize);
        $data = openssl_decrypt(substr($data, $ivSize), $method, $key, OPENSSL_RAW_DATA, $iv);

        return $data;
    }





    public function rtloauthsend($api,$username,$order_id,$domain,$secret_key,$pid) {
        $url = 'http://www.rtl-theme.com/oauth/';
        $pid = $pid;
        if($pid == 'newproduct') {
            $curl_args = array(
                    'method' => 'POST',
                    'timeout' => 45,
                    'redirection' => 5,
                    'httpversion' => '1.0',
                    'blocking' => true,
                    'headers' => array(),
                    'body' => array(
                                    'api' => $api,
                                    'username' => $username,
                                    'order_id' => $order_id,
                                    'domain' => $domain,
                                    'secretkey' => $secret_key
                                ),
                    'cookies' => array()
                    );
        } else {
            $curl_args = array(
                    'method' => 'POST',
                    'timeout' => 45,
                    'redirection' => 5,
                    'httpversion' => '1.0',
                    'blocking' => true,
                    'headers' => array(),
                    'body' => array(
                                    'api' => $api,
                                    'username' => $username,
                                    'order_id' => $order_id,
                                    'domain' => $domain,
                                    'secretkey' => $secret_key,
                                    'pid' => $pid
                                ),
                    'cookies' => array()
                    );
        }

        $res = wp_remote_post( $url, $curl_args );
        
        if(WP_DEBUG == true) {
            if ( is_wp_error( $res ) ) {
               $error_message = $res->get_error_message();
               echo "Something went wrong: $error_message";
            } else {
               echo '<pre style="direction:ltr;text-align:left">';
               print_r( $res );
               echo '</pre>';
            }
        }
        return $res['body'];
    }


